    private void inciarJuegoBotonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_inciarJuegoBotonActionPerformed
        String premio = premioEntrada.getText();
        Object modoDeJuego_O = modoDejuegoEntrada.getSelectedItem();
        String modoDeJuego = modoDeJuego_O.toString();
        Bingo.iniciarJuego(modoDeJuego,premio);
    }//GEN-LAST:event_inciarJuegoBotonActionPerformed

